import React, { Component } from 'react'
import axios from 'axios';
import './style.css';
import Controls from "./Controls.js"

var monster1 = require("./images/monster1.png");
export default class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      intro: false,
      playerHP:50,
      monster: {},
      URL: 'https://quizlet.com/296829048/repaso-del-subjuntivo-2-crs-palabras-indefinidas-csi-flash-cards/',
      quizletSet: require("./dummyQuizlet").default,
      monsterHP: 20,
      monsterAT: 20,
      questionObject: {
        correctIndex: 0,
        buttons: ["s","s","s","s"]
      }
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.generateQuestion = this.generateQuestion.bind(this);
    this.shuffleArray = this.shuffleArray.bind(this);
    this.generateMonster= this.generateMonster.bind(this);

    this.clickOne= this.clickOne.bind(this);
    this.clickTwo= this.clickTwo.bind(this);
    this.clickThree= this.clickThree.bind(this);
    this.clickFour= this.clickFour.bind(this);
  }
  shuffleArray(array) {
    for (var i = array.length - 1; i > 0; i--) {
        var j = Math.floor(Math.random() * (i + 1));
        var temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
}

  generateMonster(){
    axios.get('http://localhost:5000/monsters')
    .then(data => {
      this.setState({monster: data.data,
      monsterHP: data.data.hp,
      monsterAT: data.data.at,});
    })
    .catch(error => {
      console.log(error);
    });
  }
  generateQuestion()
  {
    var index = Math.floor(Math.random() * (this.state.quizletSet.length-1));
    let answer = this.state.quizletSet[index].definition;
    let wronganswer1 = this.state.quizletSet[Math.floor(Math.random() * (this.state.quizletSet.length-1))].definition;
    let wronganswer2 = this.state.quizletSet[Math.floor(Math.random() * (this.state.quizletSet.length-1))].definition;
    let wronganswer3 = this.state.quizletSet[Math.floor(Math.random() * (this.state.quizletSet.length-1))].definition;
    var answers = [answer,wronganswer1,wronganswer2,wronganswer3];
    this.shuffleArray(answers);
    this.setState({
      questionObject: {
        correctIndex: index,
        buttons: answers
      }
      })
  }
  componentDidMount() {
    //your write request axios.write(blah blah blah)
    //have it log to console, then check dev tools to see if it worked
    //nothing will show up in UI, this is just for you to test server, don't commit to main branch
    this.generateQuestion();
    axios.get('http://localhost:5000/monsters')
    .then(data => {
      this.setState({monster: data.data});
    })
    .catch(error => {
      console.log(error);
    });
    //axios get request for quizlet set, set quizletSet to what backend returns

  }
  handleChange(event) {
    this.setState({ URL: event.target.value });
  }
  handleSubmit(event) {


    alert('A name was submitted: ' + this.state.URL);
    event.preventDefault();
    this.setState({ intro: false })
    //get quizlet from user
    axios.get('http://localhost:5000/quizlet?url='+this.state.URL)
    .then(data => {
      this.setState({quizletSet: data.data,});
      console.log(data);
    })
    .catch(error => {
      console.log(error);
    });
  }
  clickOne(){
    if(this.state.quizletSet[this.state.questionObject.correctIndex].definition==this.state.questionObject.buttons[0])
    {
      //correct answer
      console.log("correct answer");
      this.setState({
        monsterHP: this.state.monsterHP - 5
      })
    }
    else {
      this.setState({
        playerHP: this.state.playerHP - 5
      })
      //wrong answer
    }
    if(this.state.monsterHP <= 0)
    {
      this.generateMonster();
    }
    this.generateQuestion();

  }
  clickTwo(){
    if(this.state.quizletSet[this.state.questionObject.correctIndex].definition==this.state.questionObject.buttons[1])
    {
      //correct answer
      console.log("correct answer");
      this.setState({
        monsterHP: this.state.monsterHP - 5
      })
    }
    else {
      this.setState({
        playerHP: this.state.playerHP - 5
      })
      //wrong answer
      console.log("wrong answer");
    }
    if(this.state.monsterHP <= 0)
    {
      this.generateMonster();
    }
    this.generateMonster();
  }
  clickThree(){
    if(this.state.quizletSet[this.state.questionObject.correctIndex].definition==this.state.questionObject.buttons[2])
    {
      //correct answer
      console.log("correct answer");
      this.setState({
        monsterHP: this.state.monsterHP - 5
      })
    }
    else {
      this.setState({
        playerHP: this.state.playerHP - 5
      })
      //wrong answer
      console.log("wrong answer");
    }
    if(this.state.monsterHP <= 0)
    {
      this.generateMonster();
    }
    this.generateMonster();
  }
  clickFour(){
    if(this.state.quizletSet[this.state.questionObject.correctIndex].definition==this.state.questionObject.buttons[3])
    {
      //correct answer
      console.log("correct answer");
      this.setState({
        monsterHP: this.state.monsterHP - 5
      })
    }
    else {
      this.setState({
        playerHP: this.state.playerHP - 5
      })
      //wrong answer
      console.log("wrong answer");
    }
    if(this.state.monsterHP <= 0)
    {
      this.generateMonster();
    }
    this.generateMonster();
  }
  render() {
    return (
      <div className="app-container">
        {this.state.intro ?
          <div className="form-container">
            <label>
              Enter the Quizlet URL :
            </label>
            <div className="input">
              <input type="text" value={this.state.URL} onChange={this.handleChange} />
            </div>
            <div className="button">
              <button type="submit" onClick={this.handleSubmit} className="btn btn-primary">Submit</button>
            </div>
          </div>
          :
          <div className="combat-container">
            <div className="monster-container">
              <div><img className='image' src={monster1} height="300" width="300" /></div>
              <div className="monster-stats">
                <div className='name'>{this.state.monster.name}</div>
                <div className='monster-hp'>HP: {this.state.monsterHP}</div>
                <div className='monster-at'>AT: {this.state.monsterAT}</div>
              </div>
              <div className='flavor-text'>{}</div>
            </div>
            <div className="question-container">
            {"What is "+this.state.quizletSet[this.state.questionObject.correctIndex].term+"?"}
              <Controls qObject = {this.state.questionObject}
                        clicks = {[this.clickOne,this.clickTwo,this.clickThree,this.clickFour]}/>
            </div>
          </div>}
      </div>
    )
  }
}
