import React, { Component } from 'react'
import axios from 'axios';
import './style.css';
import Controls from "./Controls.js"

var monster1 = require("./images/monster1.png");
export default class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      intro: false,
      monster: {},
      URL: 'https://quizlet.com/296829048/repaso-del-subjuntivo-2-crs-palabras-indefinidas-csi-flash-cards/',
      quizletSet: require("./dummyQuizlet").default,
      monsterHP: 20,
      monsterAT: 20,
      questionObject: {}
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.generateQuestion = this.generateQuestion.bind(this);
    this.shuffleArray = this.shuffleArray.bind(this);
    this.generateMonster= this.generateMonster.bind(this);
    this.generateQuestion();
    this.generateMonster();
  }
  shuffleArray(array) {
    for (var i = array.length - 1; i > 0; i--) {
        var j = Math.floor(Math.random() * (i + 1));
        var temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
}

  generateMonster(){
    axios.get('http://localhost:5000/monsters')
    .then(data => {
      this.setState({monster: data.data,
      monsterHP: data.data.hp,
      monsterAT: data.data.at,});
    })
    .catch(error => {
      console.log(error);
    });
  }
  generateQuestion()
  {
    let index = Math.floor(Math.random() * this.state.quizletSet.length-1);
    let correctbutton = Math.random() * 4;
    let answer = this.state.quizletSet[index].definition + "!answer!";
    let wronganswer1 = this.state.quizletSet[Math.floor(Math.random() * this.state.quizletSet.length-1)].definition;
    let wronganswer2 = this.state.quizletSet[Math.floor(Math.random() * this.state.quizletSet.length-1)].definition;
    let wronganswer3 = this.state.quizletSet[Math.floor(Math.random() * this.state.quizletSet.length-1)].definition;
    let answers = [answer,wronganswer1,wronganswer2,wronganswer3];
    this.shuffleArray(answers);
    console.log(answers);
  }
  componentDidMount() {
    //your write request axios.write(blah blah blah)
    //have it log to console, then check dev tools to see if it worked
    //nothing will show up in UI, this is just for you to test server, don't commit to main branch

    axios.get('http://localhost:5000/monsters')
    .then(data => {
      this.setState({monster: data.data});
      console.log(this.state.monster);
    })
    .catch(error => {
      console.log(error);
    });
    //axios get request for quizlet set, set quizletSet to what backend returns

  }
  handleChange(event) {
    this.setState({ URL: event.target.value });
  }
  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.URL);
    event.preventDefault();
    this.setState({ intro: false })
    //get quizlet from user
    axios.get('http://localhost:5000/quizlet?url='+this.state.URL)
    .then(data => {
      this.setState({quizletSet: data.data,});
      console.log(data);
    })
    .catch(error => {
      console.log(error);
    });
  }
  render() {
    return (
      <div className="app-container">
        {this.state.intro ?
          <div className="form-container">
            <label>
              Enter the Quizlet URL :
            </label>
            <div className="input">
              <input type="text" value={this.state.URL} onChange={this.handleChange} />
            </div>
            <div className="button">
              <button type="submit" onClick={this.handleSubmit} className="btn btn-primary">Submit</button>
            </div>
          </div>
          :
          <div className="combat-container">
            <div className="monster-container">
              <div><img className='image' src={monster1} height="300" width="300" /></div>
              <div className="monster-stats">
                <div className='name'>{this.state.monster.name}</div>
                <div className='monster-hp'>HP: {this.state.monsterHP}</div>
                <div className='monster-at'>AT: {this.state.monsterAT}</div>
              </div>
              <div className='flavor-text'>This is where the flavor text goes</div>
            </div>
            <div className="question-container">
              <Controls/>
            </div>
          </div>}
      </div>
    )
  }
}
